import React from 'react'
import Edit from './Edit'

class UserEdit extends React.Component {
    state = {
        user: {}
    }

    componentDidMount = () => {
        fetch(`/api/users/${this.props.match.params.userId}`)
            .then(res => res.json)
            .then(user => {
                this.setState({user})
            })
    }

    render() {
        return (
            <Edit user={this.state.user}/>
        )
    }
}

export default UserEdit
