import React from 'react'

class NewWorkout extends React.Component {
    state = {
        exercises: [],
        exercise: []
    }

    getData = () => {
        fetch("/api/exercise")
            .then(res => res.json())
            .then(exercises => this.setState({exercises}))
    }


    componentDidMount = () => {
        this.getData();
    }

    handleChange = event => {
        const {name, value} = event.target
        this.setState(prev => {
            const exercise = prev.exercise.concat({[name]:value})
            return{
                exercise
            }
        })
    }

    render() {
        return (
            <div>
                <h1>New workout page</h1>
                <div>
                    <form method="POST" action="/api/workout">
                        <label /> Workout name <br />
                        <input type="text" name="name" onChange={this.handleChange} />
                        {
                            this.state.exercises.map(item => {
                                return <label>{item.name}
                                            <input key={item._id} type="checkbox" name="exerciseId" value={item._id} onChange={this.handleChange} /><br />
                                       </label>
                            })
                        }
                        <button>Submit</button>
                    </form>

                </div>
            </div>
        )
    }
}

export default NewWorkout
