import React from 'react'
import Container from './Container'

class Exercises extends React.Component {
    state = {
        exercises: [],
        name: "",
        search: "",
        foundExercises: []
    }

    componentDidMount = () => {
        fetch("/api/exercise")
            .then(res => res.json())
            .then(exercises => this.setState({ exercises }))
    }

    handleChange = event => {
        const { name, value }  = event.target;
        this.setState({[name]:value})
    }

    handleSearch = () => {
        const search = this.state.search
        const exercises = this.state.exercises 
        const foundExercises = []
        exercises.forEach(exercise => {
            const searchString = search.toLocaleLowerCase()
            const name = exercise.name.toLocaleLowerCase()
            if(name.includes(searchString)){
                foundExercises.push(exercise)
            } 
        })
        this.setState({foundExercises: foundExercises})
    }

    render() {
        const foundExercises = this.state.foundExercises
        let exercises = this.state.exercises
        if(foundExercises.length > 0) {
            exercises = foundExercises
        } 
        return (
            <Container 
                exercises={exercises}
                handleSearch={this.handleSearch}
            />
        )
    }
}

export default Exercises
