import React from 'react'

class Exercises extends React.Component {
    state = {
        exercises: [],
        name: ""
    }

    componentDidMount = () => {
        fetch("/api/exercise")
            .then(res => res.json())
            .then(exercises => this.setState({ exercises }))
    }

    handleChange = event => {
        const { name, value }  = event.target;
        this.setState({[name]:value})
    }

    handleSubmit = event => {
        event.preventDefault();

        const exercise = this.state.name
        console.log(exercise)
        fetch("/api/exercise", {
            method: "POST",
            body: JSON.stringify(exercise),
            headers: {
                'Content-Type': 'application/json'
            }
        }).then(res => res.json())
        .then(response => console.log("success:", JSON.stringify(response)))
        .catch(error => console.log("error:", error));
    }

    render() {
        const exercises = this.state.exercises;
        return (
            <div className="container exs-div">
                <div className="jumbotron">
                    <div className="jumbo-content">
                        <h1 className="wo-heading">Exercises</h1>
                        <p className="wo-p">View our hand-picked selection of exercises</p>
                        <a href="/exercise/new" className="btn btn-add-wo">Add a new workout</a>
                    </div>
                </div> 
            </div>
        )
    }
}

export default Exercises
