import React from 'react'

class NewWorkout extends React.Component {
    state = {
        query: '',
        exercises: [],
        searchString: []
    }

    getData = () => {
        fetch("/api/exercise")
            .then(res => res.json())
            .then(exercises => this.setState({
                exercises: exercises,
                searchString: exercises
            }))
    }

    handleSearchChange = event => {
        this.setState({
            query: event.target.value
        }, ()=>{
            this.filterArray();
        })
    }

    filterArray = () => {
        let searchString = this.state.query;
        let responseData = this.state.exercises;

        if(searchString.length > 0){
            responseData = responseData.filter(searchString);
            this.setState({responseData})
        }
    }

    componentDidMount = () => {
        this.getData();
    }

    render() {
        console.log(this.state)
        return (
            <div>
                <h1>New workout page</h1>
                <form>
                    <input type="text" placeholder="Search" onChange={this.handleSearchChange} />
                </form>
                <div>
                    {
                        this.state.exercises.map(item => {
                            return <div>{item.name}</div>
                        })
                    }
                </div>
            </div>
        )
    }
}

export default NewWorkout
