import React from 'react'
import WorkoutsHeading from './WorkoutsHeading'

class Workout extends React.Component {
    state = {
        workouts: [],
        search: "",
        foundWorkouts: []
    }

    componentDidMount = () => {
        fetch("/api/workout")
            .then(res => res.json())
            .then(workouts => this.setState({workouts}))
    }

    handleChange = event => {
        const { name, value } = event.target
        this.setState({[name]:value})
    }

    handleSearch = () => {
        const theSearch = this.state.search
        const workouts = this.state.workouts
        const foundWorkouts = []
        workouts.forEach(workout => {
            let name = workout.name.toLowerCase()
            let search = theSearch.toLowerCase();
            if(name.includes(search)){
                foundWorkouts.push(workout)
            }
        })
        this.setState({foundWorkouts:foundWorkouts})
    }

    render() {
        const workouts = this.state.workouts
        const foundWorkouts = this.state.foundWorkouts
        if(foundWorkouts.length === 0){
            return (
                <div>
                    <WorkoutsHeading />
                    <div className="container wos-container">
                        <h1>All Workouts</h1>
                        <div className="form-group">
                            <input type="text" name="search" value={this.state.search} onChange={this.handleChange} />
                            <button className="btn btn-primary" onClick={this.handleSearch}>Search</button>
                        </div>
                        <div className="row">
                            {workouts.map(workout => (
                                <div key={workout._id}  className="col-lg-3 col-sm-6">
                                    <div className="card wo-card">
                                        <img class="card-img-top" src="https://images.unsplash.com/photo-1521805103424-d8f8430e8933?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=500&q=60"/>
                                        <div className="card-body">
                                            <h5 className="card-title">{workout.name}</h5>
                                            <p className="card-text">Description of workout</p>
                                            <a href={`/workout/${workout._id}`} class="btn wo-sm-btn">See More</a>
                                        </div>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            )
        } else {
            return (
                <div>
                    <WorkoutsHeading />
                    <div className="container wos-container">
                        <h1>All Workouts</h1>
                        <div className="form-group">
                            <input type="text" name="search" value={this.state.search} onChange={this.handleChange} />
                            <button className="btn btn-primary" onClick={this.handleSearch}>Search</button>
                        </div>
                        <div className="row">
                            {foundWorkouts.map(workout => (
                                <div key={workout._id}  className="col-lg-3 col-sm-6">
                                    <div className="card wo-card">
                                        <img class="card-img-top" src="https://images.unsplash.com/photo-1521805103424-d8f8430e8933?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=500&q=60"/>
                                        <div className="card-body">
                                            <h5 className="card-title">{workout.name}</h5>
                                            <p className="card-text">Description of workout</p>
                                            <a href={`/workout/${workout._id}`} class="btn wo-sm-btn">See More</a>
                                        </div>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            )
        }

    }
}

export default Workout
