import React from 'react'

class NewWorkout extends React.Component {
    state = {
        exercises: [],
        exercise: [],
        repSet: []
    }

    getData = () => {
        fetch("/api/exercise")
            .then(res => res.json())
            .then(exercises => this.setState({exercises}))
    }


    componentDidMount = () => {
        this.getData();
    }

    handleChange = event => {
        console.log(event.target.type)
        const {name, value} = event.target
        this.setState(prev => {
            const exercise = prev.exercise.concat({[name]:value})
            return{
                exercise
            }
        })
    }

    render() {
        console.log(this.state)
        return (
            <div className="top-div">
                <h1>New workout page</h1>
                <div>
                    <form method="POST" action="/api/workout">
                        <label /> Workout name <br />
                        <input type="text" name="name" onChange={this.handleChange} />
                        {
                            this.state.exercises.map(item => {
                                return (
                                        <div>
                                            <label key={item._id}>{item.name} 
                                            <input name="reps" type="number" placeholder="reps" onChange={this.handleChange} />
                                            <input name="sets" type="number" placeholder="sets" onChange={this.handleChange} />
                                            <input  type="checkbox" name="exerciseId" value={item._id} onChange={this.handleChange} />
                                            </label>

                                        </div>

                            )})
                        }

                        <button>Submit</button>
                    </form>

                </div>
            </div>
        )
    }
}

export default NewWorkout
